import math
try:
    expr = input("Masukkan ekspresi: ")
    allowed_chars = '0123456789+-*/(). %'
    if not all(c in allowed_chars or c in dir(math) for c in expr):
        print("Karakter tidak diizinkan.")
    else:
        print(f"Hasil: {eval(expr, {'__builtins__': {}, 'math': math}, math.__dict__)}")
except Exception as e:
    print(f"Error: {e}")
