import requests
try:
    word = input('Kata (EN): ')
    if not word:
        print('Tidak ada kata.')
        exit()
    r = requests.get(f'[https://api.dictionaryapi.dev/api/v2/entries/en/](https://api.dictionaryapi.dev/api/v2/entries/en/){word}')
    if r.status_code != 200:
        print(f"Kata '{word}' tidak ditemukan.")
        exit()

    data = r.json()[0]
    print(f"--- Definisi: {data['word']} ({data['phonetic']}) ---")
    for meaning in data['meanings']:
        print(f"
({meaning['partOfSpeech']}):")
        for i, definition in enumerate(meaning['definitions'][:3]):
            print(f"  {i+1}. {definition['definition']}")
except Exception as e:
    print(f'Error: {e}')
