import os
try:
    filename = input("Nama file (bisa partial): ")
    print(f"Mencari '{filename}' di . ...")
    count = 0
    for root, dirs, files in os.walk('.'):
        for name in files + dirs:
            if filename in name:
                print(os.path.join(root, name))
                count += 1
    print(f"
Ditemukan {count} hasil.")
except Exception as e:
    print(f"Error: {e}")
