import os
try:
    filename = input("File: ")
    n_lines = int(input("Jumlah baris (default 10): ") or 10)
    if not os.path.exists(filename):
        print(f"Error: File '{filename}' tidak ditemukan.")
        exit()
    with open(filename, 'r') as f:
        for i in range(n_lines):
            line = f.readline()
            if not line:
                break
            print(line.strip())
except Exception as e:
    print(f"Error: {e}")
