commands.default:
import random
angka_rahasia = random.randint(1, 100)
tebakan = 0
jumlah_tebakan = 0
print("Game Tebak Angka (1-100). Ketik 'exit' untuk keluar.")
while tebakan != angka_rahasia:
    try:
        pil = input("Tebakanmu: ")
        if pil == 'exit':
            print(f"Angkanya adalah {angka_rahasia}")
            break
        tebakan = int(pil)
        jumlah_tebakan += 1
        if tebakan < angka_rahasia:
            print("Terlalu kecil!")
        elif tebakan > angka_rahasia:
            print("Terlalu besar!")
        else:
            print(f"Selamat! Kamu benar! Angkanya adalah {angka_rahasia}")
            print(f"Jumlah tebakan: {jumlah_tebakan}")
    except ValueError:
        print("Masukkan angka yang valid.")
