import random
options = ['gunting', 'batu', 'kertas']
kom = random.choice(options)
pil = input("Pilihanmu (gunting/batu/kertas): ").lower()
print(f"Komputer memilih: {kom}")
if pil == kom:
    print("Hasil: Seri!")
elif (pil == 'batu' and kom == 'gunting') or (pil == 'gunting' and kom == 'kertas') or (pil == 'kertas' and kom == 'batu'):
    print("Hasil: Kamu Menang!")
elif pil in options:
    print("Hasil: Kamu Kalah!")
else:
    print("Pilihan tidak valid.")
