import json
import os
F = '.todo.json'
if not os.path.exists(F):
    print('Todo list kosong.')
    exit()
try:
    with open(F, 'r') as f:
        todos = json.load(f)
    if not todos:
        print('Todo list kosong.')
    else:
        print("--- DAFTAR TUGAS ---")
        for i, todo in enumerate(todos):
            print(f' {i+1}. {todo}')
except Exception as e:
    print(f'Error: {e}')
